import { EventEmitter, Injectable } from "@angular/core";
import { Subject } from "rxjs";
import { Ingredient } from "../shared/ingredient.model";
import { ShoppingListService } from "../shopping-list/shopping-list.service";
import { Recipes } from "./recipe.model";
@Injectable()
export class RecipeService{
  recipeChanged = new Subject<Recipes[]>();
    // recipes: Recipes[] = [
    //     new Recipes('French Fries', 'This is simply a test', 'data:image/jpeg;base64,',[
    //       new Ingredient('Meat',1),
    //       new Ingredient('French Fries',20)
    //     ]),
    //     new Recipes('Burger', 'This is simply a new recipe', 'data:image/jpeg;base64,',[
    //       new Ingredient('Meat Balls',1),
    //       new Ingredient('Burger',2)
    //     ])
    // ];  

      constructor(private slService:ShoppingListService){}
  private recipes: Recipes[] =[];
    //way to get recipe
      getRecipes(){
          return this.recipes.slice();//return a new array which is exact copy of this array
      }
      getRecipe(index:number){
        return this.recipes[index];
      }
      addIngredientsToShoppingList(ingredients:Ingredient[]){
        this.slService.addIngredients(ingredients);
      }

      addRecipe(recipe:Recipes){
        this.recipes.push(recipe);
        this.recipeChanged.next(this.recipes.slice());
      }
      updateRecipe(index:number,newRecipe:Recipes){
        this.recipes[index]  = newRecipe;
        this.recipeChanged.next(this.recipes.slice());
      }

      deleteRecipe(index:number){
        this.recipes.splice(index,1);
        this.recipeChanged.next(this.recipes.slice());
      }

      setRecipes(recipes:Recipes[]){
        this.recipes = recipes;
        this.recipeChanged.next(this.recipes.slice());
      }
}
